mapper.select:
SELECT MEMBER_NO AS MEMBER_NO ,MEMBER_ID AS MEMBER_ID ,MEMBER_NAME AS MEMBER_NAME ,MEMBER_NICKNAME AS MEMBER_NICKNAME ,CASE WHEN MEMBER_TYPE = 1 THEN '산책등록회원' WHEN MEMBER_TYPE = 2 THEN '관리자' ELSE '일반회원' END AS MEMBER_TYPE ,MEMBER_EMAIL AS MEMBER_EMAIL ,TO_CHAR(MEMBER_CREATE_DATE, 'YYYY-MM-DD HH24:MI:SS') AS MEMBER_CREATE_DATE ,MEMBER_WARN AS MEMBER_WARN ,MEMBER_IS AS MEMBER_IS FROM MEMBER WHERE 1=1
AND MEMBER_ID = #{srchVal}
AND MEMBER_NAME = #{srchVal}
AND MEMBER_NICKNAME = #{srchVal}
AND MEMBER_TYPE = #{memberType}
AND MEMBER_IS = #{memberIs}
ORDER BY MEMBER_NO DESC

SELECT A.POST_ID AS POST_ID ,A.POST_CONTENT AS POST_CONTENT ,TO_CHAR(A.POST_CREATE_DATE, 'YYYY-MM-DD HH24:MI:SS') AS POST_CREATE_DATE ,B.MEMBER_NAME AS MEMBER_NAME ,B.MEMBER_ID AS MEMBER_ID ,A.POST_SHOW_YN AS POST_SHOW_YN ,(SELECT COUNT(C.POST_ID) FROM REPLY C WHERE A.POST_ID = C.POST_ID) AS REPLY_CNT FROM POST A LEFT OUTER JOIN MEMBER B ON A.MEMBER_NO = B.MEMBER_NO WHERE 1=1
AND B.MEMBER_ID = #{srchVal}
AND B.MEMBER_NAME = #{srchVal}
AND A.POST_CONTENT LIKE '%'||#{srchVal}||'%'
AND A.POST_SHOW_YN = #{postShowYn}
ORDER BY A.POST_ID DESC

SELECT A.ROUTE_NO AS ROUTE_NO ,A.ROUTE_NAME AS ROUTE_NAME ,A.ROUTE_DEPARTURES AS ROUTE_DEPARTURES ,A.ROUTE_DESTINATION AS ROUTE_DESTINATION ,A.ROUTE_PUBLIC AS ROUTE_PUBLIC ,B.MEMBER_ID AS MEMBER_ID ,NVL(C.CNT_ROUTE_RATING,0) AS ROUTE_RATING_CNT ,NVL(C.AVG_ROUTE_RATING_SCORE,0) AS ROUTE_RATING_SCORE_AVG FROM WALK_ROUTE A LEFT JOIN MEMBER B ON A.MEMBER_NO = B.MEMBER_NO LEFT JOIN ( SELECT TRUNC(AVG(NVL(ROUTE_RATING_SCORE,0))) AS AVG_ROUTE_RATING_SCORE ,COUNT(ROUTE_NO) AS CNT_ROUTE_RATING ,ROUTE_NO AS ROUTE_NO FROM ROUTE_RATING GROUP BY ROUTE_NO ) C ON A.ROUTE_NO = C.ROUTE_NO WHERE 1=1
AND A.ROUTE_NO = #{srchVal}
AND A.ROUTE_NAME = #{srchVal}
AND A.ROUTE_DEPARTURES = #{srchVal}
AND A.ROUTE_DESTINATION = #{srchVal}
AND B.MEMBER_ID = #{srchVal}
AND A.ROUTE_PUBLIC = #{routePublic}
ORDER BY A.ROUTE_NO DESC
